import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';

class LoginForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
		email: '',
		password: ''
    };
    this.submitLoginForm = this.submitLoginForm.bind(this);
  }


	submitLoginForm(e) {    
		e.preventDefault();

		const fields = document.querySelectorAll('input');
		const fieldName = "";

		fields.forEach(field => {

			if (field.name == "email") { // validate email
				if (!field.name.valid) {
					if (field.validity.valueMissing || field.validity.typeMismatch) {
						document.getElementById(`${field.name}Error`).textContent = "not correct for email address";
					}
				}
			}

			if (field.name == "password") { // validate password
				if (!field.name.valid) {
					if (field.validity.valueMissing || field.validity.tooLong || field.validity.tooShort) {
						document.getElementById(`${field.name}Error`).textContent = "please use at least 6 - 12 characters";
					}
				}
			}

		});
	}


	render() {
			return (
				<form onSubmit={this.submitLoginForm} noValidate className="form-sigin">
					<div className="text-center mb-4">
						<img className="mb-4" src="/Logo.png" alt="" width="330" height="240" />

					</div>

					<label htmlFor="inputEmail">Email address</label>
					<div className="form-label-group">
						<input 
							type="email" 
							name="email" 
							ref={email => this.email = email} 
							className="form-control" 
							id="inputEmail"
							required  
						/>
					</div>
					<div className="error" id="emailError" />

					<label htmlFor="inputPassword">Password</label>
					<div className="form-label-group">
						<input 
							type="password" 
							name="password" 
							maxLength="12" 
							minLength="6" 
							className="form-control" 
							placeholder="Password" 
							id="inputPassword"
							required="" 
						/>
					</div>
					<div className="error" id="passwordError" />

					<div className="checkbox mb-3">
						<label>
							<input type="checkbox" value="remember-me" className="checkmark" /> Remember me
						</label>
					</div>
					<button className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
				</form>

			);

		}
	}

	class App extends React.Component {
		render() {
			return (
				<div className="container">
					<LoginForm />
					</div>
			);
		}
	}

ReactDOM.render(<App />, document.getElementById('root'));
