import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';

class LoginForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
		email: '',
		password: ''
    };
    this.submitLoginForm = this.submitLoginForm.bind(this);
  }


	submitLoginForm(e) {    
		e.preventDefault();

		const fields = document.querySelectorAll('input');
		const fieldName = "";

		fields.forEach(field => {

			if (field.name == "email") {
				if (!field.name.valid) {
					if (field.validity.valueMissing || field.validity.typeMismatch) {
						document.getElementById(`${field.name}Error`).textContent = "not correct for email address";
					}
				}
			}

			if (field.name == "password") {
				if (!field.name.valid) {
					if (field.validity.valueMissing || field.validity.tooLong || field.validity.tooShort) {
						document.getElementById(`${field.name}Error`).textContent = "please use at least 6 - 12 characters";
					}
				}
			}
			/*const isInputValid = this.InputErrors(field);

			if (!isInputValid) {
				isFormValid = false;
			}*/
		});
		//this.showLoginErrors();
	}

	showLoginErrors() {
		const inputs = document.querySelectorAll('input');
		let isFormValid = true;

		inputs.forEach(input => {

		const isInputValid = this.InputErrors(input);

			if (!isInputValid) {
				isFormValid = false;
			}
		});

		return isFormValid;
	}

	InputErrors(input) {
		const name = input.name;
		const validity = input.validity;
		const label = document.getElementById(`${name}Label`).textContent;
		const error = document.getElementById(`${name}Error`);

		const isPassword = name.indexOf('password') !== -1;

		if (!validity.valid) {
			if (validity.typeMismatch) {
				error.textContent = `${label} should be a valid email address`; 
			} else if (isPassword && validity.patternMismatch) {
				error.textContent = `${label} should be longer than 8 chars`; 
			} 
			return false;
		}

		error.textContent = '';
		return true;
	}

	render() {
			return (
			<form onSubmit={this.submitLoginForm} noValidate>
				<div className="form-group">
					<label id="emailLabel">Email</label>
						<input type="email" name="email"
							ref={email => this.email = email}
							className="form-control" required />
					<div className="error" id="emailError" />
				</div>

				<div className="form-group">
					<label id="passwordLabel">Password</label>
					<input type="password" name="password" maxLength="12" minLength="6"
						className="form-control" pattern=".{9,}" required />
					<div className="error" id="passwordError" />
				</div>

				<button className="btn btn-primary">Submit</button>
			</form>
			);
		}
	}

	class App extends React.Component {
		render() {
			return (
				<div className="container">
					<LoginForm />
					</div>
			);
		}
	}

ReactDOM.render(<App />, document.getElementById('root'));
