import React, { Component } from 'react';
import ReactDOM from 'react-dom';


import './index.css';
import * as serviceWorker from './serviceWorker';
class App extends Component {

	constructor(props) {
		super(props);
		this.state = {};
	}

	render() {
		return (
			<div className="root-container">
				<div className="box-controller">
					<div className="controller">
						Login
					</div>
					<div className="controller">
						Register
					</div>
				</div>  
			</div>
		);
	}
}

class LoginBox extends Component {

  constructor(props) {
    super(props);
    this.state = { 
    	username : '',
    	password : ''
    };

    this.inputUsername = this.inputUsername.bind(this);
    this.inputPassword = this.inputPassword.bind(this);
  }

  inputUsername(e) {
  	//console.warn(e.target.value);
  	this.setState({
  		username : e.target.value
  	});
  }

  inputPassword(e) {
  	this.setState({
  		password : e.target.value
  	});
  }

  submitLogin(e) {
  	console.log(this.state.username + "  " + this.state.password);
  }


  render() {

    return (
			<div className="inner-container">
				<div className="box">
					<div className="input-group">
						<label htmlFor="username">Username</label>
						<input onChange={this.inputUsername} type="text" name="username" className="login-input" placeholder="Username" />
					</div>

					<div className="input-group">
						<label htmlFor="password">Password</label>
						<input onChange={this.inputUsername} type="text" name="password" className="login-input" placeholder="Password" />
					</div>

					<button type="button" className="login-btn" onClick={this.submitLogin.bind(this)}>Login</button>
				</div>
			</div>
	    );
	}	
}


ReactDOM.render(<App />, document.getElementById('root'));
ReactDOM.render(<LoginBox />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
