import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';

class LoginForm extends Component {
	constructor(props) {
		super(props);

		this.state = {
			email: '',
			password: '',
			emailErrorClass: 'd-none',
			passwordErrorClass : 'd-none',
			emailErrorMessage : '',
			btnClass : 'btn btn-lg btn-signin btn-block'
		};
		this.submitLoginForm = this.submitLoginForm.bind(this);
	}


	submitLoginForm(e) {    
		e.preventDefault();

		const fields = document.querySelectorAll('input');
		const errorClass = "errors font-italic";
		const validClass = "d-none";
		const emailMessage = "";
		const passwordMessage = "";
		const isEmailError = false;
		const isPasswordError = false;


		fields.forEach(field => {

			if (field.name == "email") { // validate email
				if (!field.name.valid) {
					if (field.validity.valueMissing) {
						// if error
						isEmailError = true;

					} else if (field.validity.typeMismatch) {
						// if error
						isEmailError = true;
					} else {
						// if valid
						isEmailError = false;
					}
				} else {
					// error
					isEmailError = false;
				}

				if (isEmailError) {
					emailErrorClass = "errors font-italic";
				}
				/*this.setState({
					emailErrorClass: "errors font-italic",
					emailErrorMessage : 'atay ka'
				});*/
			}


			if (field.name == "password") { // validate password
				if (!field.name.valid) {
					if (field.validity.valueMissing || field.validity.tooLong || field.validity.tooShort) {
						this.setState({
							passwordErrorClass: "errors font-italic"
						});
						document.getElementById(`${field.name}Error`).textContent = "please use at least 6 - 12 characters";
					} else {
						this.setState({
							passwordErrorClass: "d-none"
						});
					}
				} else {
					this.setState({
						passwordErrorClass: "errors font-italic"
					});
				}
			}
		});
	}


	render() {
			return (
				<form onSubmit={this.submitLoginForm} noValidate className="form-sigin">
					<div className="text-center mb-4">
						<img className="mb-4" src="/Logo.png" alt="" width="330" height="240" />
					</div>

					<label htmlFor="inputEmail">
						<h6>Email</h6>
					</label>
					<div >
						<input 
							type="email" 
							name="email" 
							ref={email => this.email = email} 
							className="input-control" 
							placeholder="Email"
							id="inputEmail"
							required  
						/>
					</div>
					<p className={this.state.emailErrorClass} id="emailError" >{this.state.emailErrorMessage}</p>

					<label htmlFor="inputPassword">
						<h6>Password</h6>
					</label>
					<div >
						<input 
							type="password" 
							name="password" 
							maxLength="12" 
							minLength="6" 
							className="input-control" 
							placeholder="Password" 
							id="inputPassword"
							required
						/>
					</div>
					<p className={this.state.passwordErrorClass} id="passwordError" />

					<div className="checkbox mb-3">
						<input type="checkbox" value="remember-me" className="checkmark" /> 
						&nbsp;&nbsp;&nbsp;
						<label>
							<h6>Remember me</h6>
						</label>

					</div>
					<button className={this.state.btnClass} type="submit">Sign In</button>
				</form>

			);
		}
	}

	class App extends React.Component {
		render() {
			return (
				<div className="container">
				<LoginForm />
				</div>
			);
		}
	}

ReactDOM.render(<App />, document.getElementById('root'));
